import re
from pathlib import Path
path = Path('/home/vlad/Source/TenantSaas/_bmad-output/planning-artifacts/epics.md')
text = path.read_text()

mapping = {}
for line in text.splitlines():
    if line.strip().startswith('| FR') and 'Epic' in line:
        parts = [p.strip() for p in line.strip('|').split('|')]
        if len(parts) >= 2:
            fr = parts[0].strip()
            rhs = parts[1]
            for epic, story in re.findall(r'Epic\s+([\d-]+),\s*Story\s*([\d\.]+)', rhs):
                mapping.setdefault(story, []).append(fr)

for k in list(mapping):
    mapping[k] = sorted(set(mapping[k]), key=lambda x: int(x.replace('FR','')))

lines = text.splitlines()
out = []
for i, line in enumerate(lines):
    out.append(line)
    if line.startswith('### Story '):
        # find So that line within next few lines
        so_that_idx = None
        for j in range(i+1, min(i+7, len(lines))):
            if lines[j].strip().startswith('So that '):
                so_that_idx = j
                break
        if so_that_idx is not None:
            # check if Implements line already exists nearby
            k = so_that_idx + 1
            while k < len(lines) and lines[k].strip() == '':
                k += 1
            if k < len(lines) and lines[k].strip().startswith('**Implements:**'):
                continue
            m = re.search(r'Story\s+([\d\.-]+):', line)
            story_id = m.group(1) if m else ''
            frs = mapping.get(story_id, [])
            if frs:
                impl = f"**Implements:** {', '.join(frs)}"
            else:
                impl = "**Implements:** None (supporting requirement)"
            out.append('')
            out.append(impl)
            out.append('**Related NFRs:** N/A')

path.write_text('\n'.join(out) + '\n')
